import React, { Component } from 'react';
import "./Style.css";

import { 
	DefaultNodeModel,
	DefaultPortModel,
	DefaultNodeFactory,
	DefaultLinkFactory,
	DefaultPortFactory,
	DiagramEngine,
	DiagramModel } from '@projectstorm/react-diagrams';
import { CanvasWidget } from '@projectstorm/react-canvas-core';
import './graph/srd.css';

export default class DDArea extends Component {
    constructor(props) {
        super(props);
		this.engine = new DiagramEngine();
		this.engine.getNodeFactories().registerFactory(new DefaultNodeFactory());
		this.engine.getLinkFactories().registerFactory(new DefaultLinkFactory());
		this.engine.getPortFactories().registerFactory(new DefaultPortFactory());
		this.model = new DiagramModel();
        //this.engine.setModel(this.model); 

	}
    render() {
        return (
            <div
					className="diagram-layer"
					onDrop={event => {
						var data = JSON.parse(event.dataTransfer.getData('storm-diagram-node'));
						//var nodesCount = Lodash.keys(this.engine.getDiagramModel().getNodes()).length; //this is used to get the number of nodes
						var node = null;
						//alert(data.name);

						node = new DefaultNodeModel(data.name, data.color);
                        node.addPort(new DefaultPortModel(true, 'in-1', 'In'));
                        node.addPort(new DefaultPortModel(false, 'out-1', 'Out'));

						var points = this.engine.getRelativeMousePoint(event);
						node.x = points.x;
						node.y = points.y;
						this.engine.getDiagramModel().addNode(node);
                        this.forceUpdate();

                    }}
                    onDoubleClick={event=>{
                        //!------------- SERIALIZING ------------------
                        const str=JSON.stringify(this.engine.getDiagramModel().serializeDiagram())
                        console.log(str);

                        //!------------- DESERIALIZING ----------------
		                //we need this to help the system know what models to create form the JSON
		                //this.engine.registerInstanceFactory(new DefaultNodeInstanceFactory());
		                //this.engine.registerInstanceFactory(new DefaultPortInstanceFactory());
		                //this.engine.registerInstanceFactory(new LinkInstanceFactory());

		                //deserialize the model
		                //var model = new DiagramModel();
		                //model.deSerializeDiagram(JSON.parse(str), this.engine);
		                //this.engine.setDiagramModel(model);
                    }}

					onDragOver={event => {
						event.preventDefault();
					}}
				>
					<CanvasWidget className="srd-demo-canvas"  engine={this.engine} />
				</div>
        )
    }
}
