import React from 'react';

import {
	DiagramWidget,
	DiagramEngine,
	DefaultNodeFactory,
	DefaultLinkFactory,
	DefaultNodeModel,
	DefaultPortModel
} from 'storm-react-diagrams';
import TrayWidget from './component/TrayWidget';
import TrayItemWidget from './component/TrayItemWidget';
import Lodash from 'lodash';
import './srd.css';

require("storm-react-diagrams/dist/style.min.css");

class DemoFive extends React.Component {
	componentWillMount() {
		this.engine = new DiagramEngine();
		this.engine.registerNodeFactory(new DefaultNodeFactory());
		this.engine.registerLinkFactory(new DefaultLinkFactory());
	}
	render() {
		return (
			<div className="content">
				<TrayWidget>
					<TrayItemWidget model={{ type: 'in' , color: "peru", name:"Concept" }} name="Concept" color="peru" />
					<TrayItemWidget model={{ type: 'out', color: "orange", name:"Asset" }} name="Asset" color="orange" />
					<TrayItemWidget model={{ type: 'in', color: "red", name:"Participant" }} name="Participant" color="red" />
					<TrayItemWidget model={{ type: 'out', color: "blue", name:"Transaction" }} name="Transaction" color="blue" />
					<TrayItemWidget model={{ type: 'in', color: "pink", name:"Event" }} name="Event" color="pink" />
					<TrayItemWidget model={{ type: 'out', color: "yellow", name:"Enumerable" }} name="Enumerable" color="yellow" />
				</TrayWidget>
				<div
					className="diagram-layer"
					onDrop={event => {
						var data = JSON.parse(event.dataTransfer.getData('storm-diagram-node'));
						//var nodesCount = Lodash.keys(this.engine.getDiagramModel().getNodes()).length; //this is used to get the number of nodes
						var node = null;
						//alert(data.name);
						if (data.type === 'in' ) {
							node = new DefaultNodeModel(data.name, data.color);
							node.addPort(new DefaultPortModel(true, 'in-1', 'In'));
						} else {
							node = new DefaultNodeModel(data.name, data.color);
							node.addPort(new DefaultPortModel(false, 'out-1', 'Out'));
						}
						var points = this.engine.getRelativeMousePoint(event);
						node.x = points.x;
						node.y = points.y;
						this.engine.getDiagramModel().addNode(node);
						this.forceUpdate();
					}}
					onDragOver={event => {
						event.preventDefault();
					}}
				>
					<DiagramWidget className="srd-demo-canvas"  diagramEngine={this.engine} />
				</div>
			</div>
		);
	}
}
export default DemoFive;
