import * as React from 'react';
import * as _ from 'lodash';
import { Application } from '../Application';
import { DefaultNodeModel } from '@projectstorm/react-diagrams';
import { CanvasWidget } from '@projectstorm/react-canvas-core';
import './style.css';
import { AdvancedPortModel } from '../custom-link/Arrow';
import { JSCustomNodeModel } from '../custom-node-js/JSCustomNodeModel'
export interface BodyWidgetProps {
	app: Application;
}

export class BodyWidget extends React.Component<BodyWidgetProps> {
	render() {
		return (
					<div
						onDrop={event => {
							var data = JSON.parse(event.dataTransfer.getData('storm-diagram-node'));

							var node = null;
							node = new DefaultNodeModel(data.name, data.color);
							switch(data.name){
								case 'Concept':
									{
										var ports = [                                                
											{ text: "Datetime day", color: "grey", isIn: false },
											{ text: "String fname", color: "RGB(192, 255, 0)", isIn: false },
											{ text: "String mname", color: "RGB(192, 255, 0)", isIn: false },
											{ text: "String lname", color: "RGB(192, 255, 0)", isIn: false },
										  ]
										break;
									}

								case 'Participant':
									{
										var ports = [                                                 
											{ text: "String color", color: "RGB(20, 20, 40)", isIn: true },
											{ text: "String On", color: "RGB(20, 20, 40)", isIn: true },
											{ text: "String Stop", color: "RGB(20, 20, 40)", isIn: true },              
											{ text: "Datetime Delay", color: "grey", isIn: true },
											{ text: "Int Space", color: "RGB(192, 255, 0)", isIn: true },
										  ]
										break;
									}
								case 'Transaction':
									{
										var ports = [                                                 
											{ text: "String color", color: "RGB(20, 20, 40)", isIn: true },
											{ text: "String On", color: "RGB(20, 20, 40)", isIn: true },
											{ text: "String Sasuke", color: "RGB(20, 20, 40)", isIn: true },             
										  ]
										break;

									}
								case 'Event':
									{
										var ports = [                                                 
											{ text: "String color", color: "RGB(20, 20, 40)", isIn: true },
											{ text: "String On", color: "RGB(20, 20, 40)", isIn: true },
											{ text: "String Stop", color: "RGB(20, 20, 40)", isIn: true },               
											{ text: "Datetime Delay", color: "grey", isIn: true },

										  ]
										break;
									}
								case 'Enumerable':
									{
										var ports = [                                                  
											{ text: "String color", color: "RGB(20, 20, 40)", isIn: true },

											{ text: "String Pitch", color: "RGB(192, 255, 0)", isIn: true },
											{ text: "String Space", color: "RGB(192, 255, 0)", isIn: true },
											{ text: "String Space", color: "RGB(192, 255, 0)", isIn: true },
										  ]
										break;
									}
								case 'Asset':
									{
										var ports = [                                                 

											{ text: "Datetime Amp", color: "grey", isIn: true },
											{ text: "Decorator Pitch", color: "RGB(192, 255, 0)", isIn: true },
											{ text: "Decorator Space", color: "RGB(192, 255, 0)", isIn: true },
											{ text: "Decorator Space", color: "RGB(192, 255, 0)", isIn: true },
										  ]
										break;
									}
							}

							node = new JSCustomNodeModel({ name: data.name, color: data.color, ports });
							node.addPort(new AdvancedPortModel(true, 'in'));
							node.addPort(new AdvancedPortModel(false, 'out'));


							var point = this.props.app.getDiagramEngine().getRelativeMousePoint(event);
							node.setPosition(point);
							this.props.app
								.getDiagramEngine()
								.getModel()
								.addNode(node);
							this.forceUpdate();
						}}
						onDragOver={event => {
							event.preventDefault();
						}}>

							<CanvasWidget className="srd-demo-canvas" engine={this.props.app.getDiagramEngine()} />

					</div>

		);
	}
}
