import { Application } from '../Application';
import React, { Component } from 'react';
import './style.css';
import {
    DefaultNodeModel,
} from '@projectstorm/react-diagrams';

import { CanvasWidget } from '@projectstorm/react-canvas-core';
import { AdvancedLinkFactory, AdvancedPortModel } from './AdvancedLink';
export interface BodyWidgetProps {
	app: Application;
}

export class BodyWidget extends React.Component<BodyWidgetProps> {
	constructor(props){
        super(props);
        this.props.app.getLinkFactories().registerFactory(new AdvancedLinkFactory());

	}

	render() {
		return (
					<div
						onDrop={event => {
							var data = JSON.parse(event.dataTransfer.getData('storm-diagram-node'));

							var node: DefaultNodeModel = null;
                            node = new DefaultNodeModel(data.name, data.color);
                            node.addPort(new AdvancedPortModel(true, 'in'));
                        	//node.addInPort('In');
							//node.addOutPort('Out');

							var point = this.props.app.getDiagramEngine().getRelativeMousePoint(event);
							node.setPosition(point);
							this.props.app
								.getDiagramEngine()
								.getModel()
								.addNode(node);
							this.forceUpdate();
						}}
						onDragOver={event => {
							event.preventDefault();
						}}>

							<CanvasWidget className="srd-demo-canvas" engine={this.props.app.getDiagramEngine()} />

					</div>

		);
	}
}
