import * as _ from 'lodash';
import { Application } from '../Application';
import React, { Component } from 'react';
import './style.css';


import createEngine, {
    DiagramModel,
    DefaultNodeModel,
    DefaultPortModel,
    DefaultLinkFactory,
    DefaultLinkPointWidget,
    DefaultLinkModel,
    DefaultLinkWidget,
    DefaultNodeFactory
} from '@projectstorm/react-diagrams';
import { DiagramEngine, LinkWidget, PointModel } from '@projectstorm/react-diagrams-core';
import { CanvasWidget } from '@projectstorm/react-canvas-core';


export interface BodyWidgetProps {
	app: Application;
}

export class AdvancedLinkModel extends DefaultLinkModel {
    constructor() {
        super({
            type: 'advanced',
            width: 4
        });
    }
}

export class AdvancedPortModel extends DefaultPortModel {
    createLinkModel(): AdvancedLinkModel | null {
        return new AdvancedLinkModel();
    }
}

const CustomLinkArrowWidget = props => {
    const { point, previousPoint } = props;

    const angle =
        90 +
        (Math.atan2(
            point.getPosition().y - previousPoint.getPosition().y,
            point.getPosition().x - previousPoint.getPosition().x
        ) *
            180) /
            Math.PI;

    //translate(50, -10),
    return (
        <g className="arrow" transform={'translate(' + point.getPosition().x + ', ' + point.getPosition().y + ')'}>
            <g style={{ transform: 'rotate(' + angle + 'deg)' }}>
                <g transform={'translate(0, -3)'}>
                    <polygon
                        points="0,10 8,30 -8,30"
                        fill={props.color}
                        onMouseLeave={() => {
                            this.setState({ selected: false });
                        }}
                        onMouseEnter={() => {
                            this.setState({ selected: true });
                        }}
                        data-id={point.getID()}
                        data-linkid={point.getLink().getID()}></polygon>
                </g>
            </g>
        </g>
    );
};

export class AdvancedLinkWidget extends DefaultLinkWidget {
    generateArrow(point: PointModel, previousPoint: PointModel): JSX.Element {
        return (
            <CustomLinkArrowWidget
                key={point.getID()}
                point={point}
                previousPoint={previousPoint}
                colorSelected={this.props.link.getOptions().selectedColor}
                color={this.props.link.getOptions().color}
            />
        );
    }

    render() {
        //ensure id is present for all points on the path
        var points = this.props.link.getPoints();
        var paths = [];
        this.refPaths = [];

        //draw the multiple anchors and complex line instead
        for (let j = 0; j < points.length - 1; j++) {
            paths.push(
                this.generateLink(
                    LinkWidget.generateLinePath(points[j], points[j + 1]),
                    {
                        'data-linkid': this.props.link.getID(),
                        'data-point': j,
                        onMouseDown: (event: MouseEvent) => {
                            this.addPointToLink(event, j + 1);
                        }
                    },
                    j
                )
            );
        }

        //render the circles
        for (let i = 1; i < points.length - 1; i++) {
            paths.push(this.generatePoint(points[i]));
        }

        if (this.props.link.getTargetPort() !== null) {
            paths.push(this.generateArrow(points[points.length - 1], points[points.length - 2]));
        } else {
            paths.push(this.generatePoint(points[points.length - 1]));
        }

        return <g data-default-link-test={this.props.link.getOptions().testName}>{paths}</g>;
    }
}

export class AdvancedLinkFactory extends DefaultLinkFactory {
    constructor() {
        super('advanced');
    }

    generateModel(): AdvancedLinkModel {
        return new AdvancedLinkModel();
    }

    generateReactWidget(event): JSX.Element {
        return <AdvancedLinkWidget link={event.model} diagramEngine={this.engine} />;
    }
}


export class BodyWidget extends React.Component<BodyWidgetProps> {
	constructor(props){
        super(props);
        this.props.getLinkFactories().registerFactory(new AdvancedLinkFactory());


	}

	render() {
		return (
					<div
						onDrop={event => {
							var data = JSON.parse(event.dataTransfer.getData('storm-diagram-node'));

							var node: DefaultNodeModel = null;
							node = new DefaultNodeModel(data.name, data.color);
                        	node.addPort(new AdvancedPortModel(true, 'in'));
                            node.addPort(new AdvancedPortModel(false, 'out'));

							var point = this.props.app.getDiagramEngine().getRelativeMousePoint(event);
							node.setPosition(point);
							this.props.app
								.getDiagramEngine()
								.getModel()
								.addNode(node);
							this.forceUpdate();
						}}
						onDragOver={event => {
							event.preventDefault();
						}}>

							<CanvasWidget className="srd-demo-canvas" engine={this.props.app.getDiagramEngine()} />

					</div>

		);
	}
}
